<!-- component -->
<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.14.0/css/all.min.css">
<style>
    .bannerFondo {
        height: 350px;
    }

</style>
<div>
    <div class="bannerFondo bg-indigo-800 bg-left-top bg-auto bg-repeat-x">
    </div>

    <div class="-mt-64">
        <div class="w-full text-center">
